#knihovnyfrom machine import Pin, I2Cimport neopixelimport timefrom scd4x import SCD4X#knihovny # připojení senzoru CO2, teploty a vlhkostii2c = I2C(0,sda=Pin(18),scl=Pin(19))sensor=SCD4X(i2c)sensor.start_periodic_measurement() # pripojení led krouzeknp = neopixel.NeoPixel(Pin(16), 24)# nastavení počtu diod pro hodnoty co2CO2_LED = list(range(0, 24))#získání hodnot z senzorudef get_value():    co2 = sensor.co2    return co2effect_done = False #efekt zapínání# Hlavní smyčka programuwhile True:    co2 = get_value() #získání dat ze senzoru    #efekt zapínání                            if not effect_done:        for i in range(24):            np[i] = (0, 100, 0)  # nastavení zelené barvy            np.write()  # aktualizace LED            time.sleep_ms(100)  # čekání 0,1 sekundy        effect_done = True  # Nastavte efekt jako dokončený#efekt pokud co2 je měnší nebo se rovná 800    if co2 <= 800:        for i in CO2_LED:            np[i] = (0, 0, 60)        np.write()#efekt pokud co2 je menší nebo se rovná 1200    elif co2 <= 1200:        for i in CO2_LED:            np[i] = (0, 60, 0)        np.write()#efekt pokud co2 je menší nebo se rovná 1500    elif co2 <= 1900:        for i in CO2_LED:            np[i] = (100, 20, 0)        np.write()#efekt pokud co2 je větší než 1500    else:        for i in range(5):            print(f"CO2(ppm): {co2}")            for j in CO2_LED:                np[j] = (60, 0, 0)            np.write()            time.sleep(0.8)            for j in CO2_LED:                np[j] = (0, 0, 0)            np.write()            time.sleep(0.8)                     print(f"CO2(ppm): {co2}")    time.sleep(0.05)                       